list.javascript:
function (context, options) {
      if (context.length > 0) {
        var out = "<ul>";
        for(var i = 0,j=context.length; i < j; i++) {
          out += "<li>";
          out += options.fn(context[i]);
          out += "</li>";
        }
        out += "</ul>";
        return out;
      } else {
        return "<p>" + options.inverse(this) + "</p>";
      }
    }

list.php:
function($context, $options) {
    $out = '';
    if (count($context) > 0) {
        $out .= '<ul>';
        foreach ($context as $row) {
            $out .= '<li>';
            $out .= $options->fn($row);
            $out .= '</li>';
        }
        $out .= '</ul>';
    } elseif (isset($options->inverse)) {
        $out .= '<p>';
        $out .= $options->inverse($options->scope);
        $out .= '</p>';
    }
    return $out;
}

